INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (1, '胡莲仙', '湘绣产业化的奠基人，原籍安徽的苏绣名家，婚后迁居湖南长沙，融合苏绣\"精细劈丝\"\"配色晕染\"技艺与本地刺绣特点，其子吴汉臣1898年创办\"吴彩霞绣坊\"。', '完整_湘绣传承人与大师表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (2, '刘爱云', '第一批国家级非遗传承人，第四届中国工艺美术大师，师从\"鬅毛针\"创始人余振辉，是湘绣\"狮虎题材\"的顶尖传人，代表作《王者风范》《饮虎》。', '完整_湘绣传承人与大师表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (3, '江再红', '出生于湖南宁乡七代刺绣世家，国家级非遗传承人，2003年创立\"长沙天心湘绣厂\"，首创\"立体绣\"技法，代表作《张家界》被人民大会堂湖南厅收藏。', '完整_湘绣传承人与大师表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (4, '李艳', '中国工艺美术大师、国家级非遗传承人，擅长\"花鸟、人物题材\"，代表作《芙蓉国里尽朝晖》，主导\"湘绣进校园\"项目。', '完整_湘绣传承人与大师表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (5, '李凯云', '中国工艺美术大师，原籍广东，专注画稿设计，擅长将工笔画意境融入绣品，代表作《雄狮》《饮虎》画稿，至今仍是湘绣教学的经典范本。', '完整_湘绣传承人与大师表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (6, '杨应修', '中国工艺美术大师，卓越的设计师与全流程宗师，20世纪70-80年代任湖南湘绣研究所所长，推动湘绣从\"传统题材\"向\"时代主题\"拓展，代表作《雄狮》《饮水虎》画稿。', '完整_湘绣传承人与大师表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (7, '刘雅', '新生代传承人，长沙市市级非遗传承人，出生于湘绣世家，曾在法国学习艺术设计，将西方现代艺术元素融入湘绣，作品《印象・芙蓉》曾入选巴黎时装周\"非遗跨界展\"。', '完整_湘绣传承人与大师表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (8, '昌妮', '\"85后\"新生代传承人代表，母亲是中国工艺美术大师李艳，2018年与母亲共同创立\"艳湘绣\"品牌，主打\"轻文创\"路线，设计湘绣元素的日常用品。', '完整_湘绣传承人与大师表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (9, '存放环境', '需存放于干燥、通风良好的环境，湿度控制在40%-60%，温度保持在18-22℃；避免潮湿，可放置干燥的樟木条防潮防虫；避免阳光长期直射，防止丝线褪色。', '完整_湘绣保养要点表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (10, '日常维护', '禁止折叠或承受重物压迫；长期存放时，应平铺于棉质垫布上，或卷成圆筒状，外层包裹无酸纸；日常绣品可定期轻轻翻晒，每次不超过1小时，置于阴凉通风处。', '完整_湘绣保养要点表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (11, '清洁方法', '表面灰尘可用柔软的毛刷轻轻拂扫，拂扫方向与绣线纹理一致；切勿进行水洗或商业干洗；如有污渍，应交由专业人员进行极其谨慎的局部处理。', '完整_湘绣保养要点表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (12, '搬运与展示', '搬运时需谨慎握持绣品边缘或装裱部位，佩戴干净棉质手套；大幅绣品需两人协作；展示时建议采用悬挂式装裱，装裱框需预留1-2厘米的\"透气空间\"。', '完整_湘绣保养要点表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (13, '基本定义', '湘绣是中国四大名绣之一，以湖南省长沙地区为核心，蕴含鲜明湘楚文化特色的刺绣工艺产品的总称，题材常融入《岳阳楼记》《桃花源记》等湖湘文化元素。', '完整_湘绣基本信息表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (14, '历史起源', '起源于湖南本地民间刺绣，长沙战国楚墓、马王堆西汉古墓出土的绣品（如\"龙凤图案绣品\"\"绢地长寿绣\"），印证其2000多年历史根基。', '完整_湘绣基本信息表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (15, '文化地位', '2006年列入第一批国家级非物质文化遗产名录（项目序号：318，编号：Ⅶ-19）；2011年获国家地理标志产品保护。', '完整_湘绣基本信息表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (16, '核心特色', '核心特色是\"绣品若画\"，以\"鬅毛针\"等针法逼真呈现物象肌理，\"苏猫湘虎\"是其标志性题材；2005年作品随\"神六\"升空，是首个进入太空的非遗刺绣品类。', '完整_湘绣基本信息表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (17, '保持绣面洁净', '刺绣前除净手外，需佩戴干净的棉质指套；工作台需铺洁净白布，绣品暂放时要用干净宣纸覆盖，防止灰尘、绒毛沾染。', '完整_湘绣操作注意事项表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (18, '保证丝线顺滑', '劈丝前先将丝线在光滑的瓷板上轻捋3-5次，理顺纹理；刺绣时若遇线体扭结，需用纹竹轻轻挑开，不可硬拉以免丝线起毛。', '完整_湘绣操作注意事项表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (19, '追求针脚均匀', '掺针的针脚长度需控制在0.5-1厘米，疏密误差不超过0.2毫米；可提前用铅笔在底料上标记针脚间距，确保\"齐、密、匀、顺\"。', '完整_湘绣操作注意事项表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (20, '控制拉线力度', '拉线时以\"底料轻微紧绷但无褶皱\"为标准，可通过练习用相同力度捏针，避免针脚松紧不一。', '完整_湘绣操作注意事项表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (21, '巧妙隐藏线头', '起针时将线头在底料背面绣线下方\"埋入2-3针\"，收针时将线头从绣线缝隙中穿入并剪断（预留1毫米线头）；双面绣需在正反两面分别隐藏线头。', '完整_湘绣操作注意事项表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (22, '具备耐心与细心', '精品绣制时，每天连续作业不超过4小时（避免视觉疲劳导致针脚偏差）；每完成10平方厘米绣面，需用放大镜检查针脚与色彩过渡。', '完整_湘绣操作注意事项表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (23, '掌握劈丝技术', '手工劈丝时，将丝线固定在劈丝板上，用纹竹沿丝线纤维方向均匀拆分，16开劈丝需分4次完成，确保每缕丝线粗细一致。', '完整_湘绣操作注意事项表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (24, '注重针法衔接', '掺针衔接处，后一针需\"覆盖前一针的1/3针脚\"，形成自然过渡；不同针法衔接需提前规划针脚走向，避免线条生硬。', '完整_湘绣操作注意事项表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (25, '素库缎', '质地紧密厚实、光泽度高且不易变形，是湘绣极品精品的首选底料，能承载密集针法与精细劈线，绣后质感挺括、立体感强。', '完整_湘绣材料与工具表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (26, '软缎', '光泽柔软、垂坠感好，多用于日常装饰类普通绣品，适配基础针法，性价比更高。', '完整_湘绣材料与工具表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (27, '透明真丝纱', '质地轻薄通透，是双面绣、双面全异绣的专用底料，能保证两面图案互不干扰，凸显绣线的细腻层次。', '完整_湘绣材料与工具表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (28, '桑蚕丝线', '光泽柔和、韧性好，是传统湘绣的核心用线。', '完整_湘绣材料与工具表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (29, '柞蚕丝线', '颜色更浓郁，常用于猛兽题材中表现毛发的厚重质感。', '完整_湘绣材料与工具表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (30, '绣架', '分大绷、中绷、小绷三类，大绷用于巨幅绣品，中绷适配大幅挂画，小绷用于小型绣品。', '完整_湘绣材料与工具表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (31, '绣花针', '型号从1号到12号不等，粗针适配金银线、粗蚕丝线，细针适配16开劈线，用于精细部位。', '完整_湘绣材料与工具表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (32, '绣剪', '多为弯头小剪，刀刃仅1-2厘米长，能精准修剪绣线末端的毛边，不会误剪相邻绣线。', '完整_湘绣材料与工具表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (33, '压条', '竹制或木质窄条，绣制前固定在绷架上，使底料紧绷且边缘不起皱。', '完整_湘绣材料与工具表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (34, '纹竹', '一头削尖的细竹棍，兼具绘图与分线功能，可在底料上勾勒图案草稿，辅助劈丝。', '完整_湘绣材料与工具表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (35, '青年创业', '\"湘见相熹\"团队由跨专业大学生组建，将湘绣鬅毛针、掺针等传统技法融入现代首饰设计，推出\"赤影掠青\"\"鹤入苍松\"等系列作品，实现非遗生活化。', '完整_湘绣现代创新表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (36, '科技赋能', '传承人潘妍打造湘绣云上博物馆，通过高清图像与详细解说，打破时空限制展示传统与现代湘绣作品；开发温感变色文房盒、蓝牙音响等\"非遗+科技\"产品。', '完整_湘绣现代创新表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (37, '跨界合作', '高校、设计机构与非遗工坊共建协同平台，形成长沙、株洲、湘潭\"湘绣创新三角区\"；与国际奢侈品牌合作推出限量手袋系列；湘绣小镇搭建研学体验基地，开发\"一日绣娘\"课程。', '完整_湘绣现代创新表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (38, '产业发展', '截至2023年底，全国湘绣相关产业手工艺人约1.8万人，湖南省占比超72%；2023年相关产业销售额增长2.3亿元，数字交易平台促成交易额达9.7亿元。', '完整_湘绣现代创新表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (39, '传承新体系', '建立12.3万亩湘绣专用桑园，实现\"一蚕一码\"全生命周期管理；82.3%的绣线工坊接入数字交易平台；研发\"湖湘七彩\"染色体系，环保植物染料使湘绣线色谱扩展至210种以上。', '完整_湘绣现代创新表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (40, '单面绣', '最常见的艺术品类，仅单面绣制图案，以素库缎为底料，适配大幅山水、人物题材，是博物馆收藏与展览的主流品类。', '完整_湘绣绣品种类表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (41, '双面绣', '底料多为透明真丝纱，正反两面绣制相同图案，绣面平整对称，常用于小型装饰画，可置于玻璃框中双面观赏。', '完整_湘绣绣品种类表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (42, '双面全异绣', '湘绣高阶技艺代表，同一底料正反两面绣制不同图案、不同色彩，需精准控制针脚避免正反串色，代表作《双面猫蝶图》。', '完整_湘绣绣品种类表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (43, '条屏', '多为4-6联的组合绣品，以卷轴形式呈现，如《四季花卉条屏》。', '完整_湘绣绣品种类表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (44, '传统日用品', '包括荷包、怀镜袋、笔插、椅垫、桌围、枕套、绣衣、绣花鞋等，兼具实用性与观赏性。', '完整_湘绣绣品种类表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (45, '现代创新日用品', '包括湘绣丝巾、围巾、抱枕、桌布、书签、笔记本封面等日常用品，让传统技艺走进年轻消费群体。', '完整_湘绣绣品种类表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (46, '传统题材', '包括走兽（狮、虎等）、花鸟（牡丹、荷花等）、山水（张家界、岳阳楼等）、人物肖像、文学典故等。', '完整_湘绣绣品种类表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (47, '现代题材', '包括红色文化、乡村振兴、国宝青铜器复刻、儿童美德故事、现代城市风貌等。', '完整_湘绣绣品种类表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (48, '掺针', '长短针脚呈阶梯状穿插，不同颜色丝线按\"浅-深-浅\"顺序交错，完美表现物象的立体形态和晕染效果，是湘绣仿真绣的基础与核心针法。', '完整_湘绣针法体系表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (49, '鬅毛针', '湘绣特有标志性针法，由余振辉在20世纪30年代创造，采用双针组合施绣，专用于表现狮、虎等动物毛发的蓬松、刚劲与立体质感。', '完整_湘绣针法体系表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (50, '旋游针', '专用于绣制动物眼球，以眼球中心点为圆心，用3-4种色线分层呈螺旋状绣制，让动物眼球看起来炯炯有神。', '完整_湘绣针法体系表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (51, '乱针绣', '针脚直斜交叉、长短不一，按物象的光影走向排列，极大增强物象的立体感与光影效果，常用于绣制山水的阴影部分。', '完整_湘绣针法体系表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (52, '平绣', '针脚平铺、纹理平整，是湘绣最基础的针法类别，包括直针、铺针、帘针等32种，适配大部分题材的基底绣制。', '完整_湘绣针法体系表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (53, '织绣', '模拟织物纹理，通过绣线交织呈现类似纺织物的质感，包括平织、交织、对织等15种，常用于绣制布料纹理。', '完整_湘绣针法体系表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (54, '网绣', '通过绣线交叉形成网状结构，多用于装饰性纹样或镂空效果，包括十字针、三角网针等11种。', '完整_湘绣针法体系表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (55, '粗绣', '使用较粗的绣线或特殊技法，呈现厚重、立体的装饰效果，包括毡环针、雀眼针等8种，多用于器物、服饰的质感表现。', '完整_湘绣针法体系表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (56, '结绣', '以绣线打结的方式形成立体结点，多用于装饰性纹样或小型图案，包括圆子针、打籽针等6种。', '完整_湘绣针法体系表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (57, '派力司针', '以0.2-0.3厘米的细密平行短线密集排列，能模拟精纺毛料的哑光质感，常用于绣制现代服饰、家居软装类作品。', '完整_湘绣针法体系表.csv', '2026-02-14 12:22:09');
INSERT INTO `knowledge_node` (`id`, `category`, `content`, `source_file`, `created_at`) VALUES (58, '蜂窝针', '借鉴蜂巢的几何结构，用绣线以六边形为单元重复绣制，形成立体网状肌理，多用于装饰画的背景、器物的镂空纹样设计。', '完整_湘绣针法体系表.csv', '2026-02-14 12:22:09');
